import { StyleSheet } from "react-native";

const rent_style = StyleSheet.create ({

    // ------------------- ( Contenedor de toda la p√°gina )

    container: {
        backgroundColor: '#a12b2b',
        width: '-webkit-fill-available',
        height: '-webkit-fill-available',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },

    // ------------------- ( Titulo del formualario )

    title: {
        margin: '10px',
        padding: '5px',
        fontSize: '16px',
        fontFamily: 'Tahoma',
        fontWeight: '600',
    },    

    // ------------------- ( Contenedor del formualario )

    form_container: {
        backgroundColor: 'white',
        height: '650px',
        margin: '10px',
        padding: '200px',
        paddingTop: '20px',
        alignItems: 'center',
        justifyContent: 'center',
        border: '2px solid black',
    },

    // -------------------  ( Contenedor del input, estilos de los inputs y textos de los inputs )

    input_container: {
        width: '270px',
    },

    input: {
        margin: '10px',
        padding: '10px',
        fontFamily: 'cursive',
        borderRadius: '5px',
        border: '2px solid #a12b2b',
    },

    input_text: {
        marginLeft: '12px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'start',
        fontFamily: 'cursive',
        fontWeight: '600',
    },

    // ------------------- ( Contenedor de los botones, contenedor de la fila de dos botones y estilos de los botones )

    button_container: {
        width: '270px',
        margin: '5px',
        padding: '5px',
    },

    row_button_container: {
        flexDirection: 'colum',
        justifyContent: 'center'
    },

    button: {
        margin: '5px',
        borderRadius: '7px',
        backgroundColor: '#a12b2b',
    },

    // ------------------- ( Estilos de los mensajes de alerta )

    message: {
        margin: '10px',
        padding: '5px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontWeight: '600',
    }

    // -------------------
    
});

export {rent_style};
