import { StyleSheet } from "react-native";

const login_style = StyleSheet.create ({

    
    // ------------------- ( Contenedor de toda la página )

    container: {
        
        backgroundColor: '#a12b2b',
        width: '100vw',
        height: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },

     // ------------------- ( Titulo del formualario )

     title: {

       
            marginTop: '10px',
            padding: '5px',
            fontSize: '16px',
            fontFamily: 'Tahoma',
            Color:'white',
            fontWeight: '600',
        
    },


    

    // ------------------- ( Contenedor del login )

    login_container: {
        backgroundColor: 'white',
        margin: '50px',
        padding: '100px',
        paddingTop: '20px',
        alignItems: 'center',
        justifyContent: 'center',
        border: '2px solid black',
    },

    // ------------------- ( Contenedor del input, estilos de los inputs y textos de los inputs )

    input_container: {
        width: '270px',
    },

    input: {
        margin: '10px',
        padding: '10px',
        fontFamily: 'Tahoma',
        border: '1px solid #a12b2b',
    },

    input_text: {
        marginLeft: '12px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'start',
        fontFamily: 'Cursive',
        fontWeight: '600',
    },

    // ------------------- ( Contenedor del input de contraseña y icono para ver contraseña )

    input_password: {
        display: 'flex',
        flexDirection: 'row',
    },

    input_icon: {
        top: '5px',
        left: '6px',
       
    },

    // ------------------- ( Contenedor de los botones y estilos de los botones )

    button_container: {
        width: '270px',
        margin: '5px',
        padding: '5px',
    },

    button: {
        margin: '5px',
        borderRadius: '7px',
        backgroundColor: '#a12b2b',
    },

    

    // ------------------- ( Estilos de los mensajes de alerta )

    message: {
        margin: '10px',
        padding: '5px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontWeight: '600',
    }

    // -------------------

});

export {login_style};
