import React, { useState, useEffect } from "react";
import Icon from '@mdi/react';
import { mdiCarDoorLock } from '@mdi/js';


import { Button, IconButton } from "react-native-paper";
import { TextInput, View, Text } from "react-native";

import { useNavigation } from '@react-navigation/native'; // Importamos la navegación "navigation"

import { login_style } from "../assets/css/login_styles"; // Importamos el "login_styles", de la carpeta "assets/css"

import { useAppContext } from "../AppContexts"; // Importa el contexto que almacena los arrays

export default function LoginScreen() {

    const { accounts, setAccounts } = useAppContext(); // Accede a los datos compartidos desde el contexto

    const navigation = useNavigation(); // Inicializamos la variable de navegación

    const [user, setUser] = useState('');
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [message, setMessage] = useState(''); 
    const [messageColor, setMessageColor] = useState(false); 

    // Función para crear cuenta
    
    const createAccount = () => {
        
        if (user === "" || name === "" || password === "") {

            setMessage("Debes ingresar todos los campos")
            setMessageColor(false);

        } else if (!/^[a-zA-Z0-9]+$/.test(user)) {

            setMessage("El usuario solo puede contener letras y números");
            setMessageColor(false);

        } else if (!/^[a-zA-Z\s]+$/.test(name)) {

            setMessage("El nombre solo puede contener letras y espacios");
            setMessageColor(false);

        } else if (!/[a-zA-Z]/.test(password) || !/[0-9]/.test(password)) {

            setMessage("La contraseña debe contener letras y números");
            setMessageColor(false);

        } else if (accounts.some((account) => account.user === user)) {

            setMessage("El usuario ya existe");
            setMessageColor(false);

        } else {

            const account = { user, name, password }; // Crear un objeto con los datos de la cuenta
            setAccounts([...accounts, account]); // Actualiza el contexto en lugar del estado local
            clearInputs();
            setMessage("Cuenta creada exitosamente");
            setMessageColor(true);
        } 
    };

    // Función para iniciar sesión
    
    const singIn = () => {

        const account = accounts.find((account) => account.user === user && account.password === password);

        if (user === "" || password === "") {

            setMessage("Debes ingresar el usuario y contraseña")
            setMessageColor(false);

        } else if (account) {

            clearInputs();
            setMessage("")
            navigation.navigate('Car')

        } else {

            setMessage("El usuario no existe");
            setMessageColor(false);
        }
    };

    // Función para limpiar los campos
 
    const clearInputs = () => {
        setUser("");
        setName("");
        setPassword("");
    };

    /*

    Imprimir el array de "accounts" en la consola

    useEffect(() => {
        console.log(accounts);
    }, [accounts]);

    */
   
    return (

        <View style={login_style.container}>

            <View style={login_style.login_container}>
                
                <View style={login_style.input_container}>

                    {/* Input User */}

                    <Text style={login_style.input_text}>Usuario</Text>

                    <TextInput 
                        style={login_style.input}
                        onChangeText={(text) => setUser(text)}
                        value={user}
                    />

                    {/* Input Name */}

                    <Text style={login_style.input_text}>Nombre</Text>

                    <TextInput 
                        style={login_style.input}
                        onChangeText={(text) => setName(text)}
                        value={name}
                    />    
                    
                    {/* Input Password */}

                    <Text style={login_style.input_text}>Contraseña</Text>

                    <View style={login_style.input_password}>

                        <TextInput 
                            style={login_style.input}
                            onChangeText={(text) => setPassword(text)}
                            value={password}
                            secureTextEntry={!showPassword}
                        />  

                        <IconButton
                            style={login_style.input_icon}
                            icon={showPassword ? 'car-door' : 'car-door-lock'}
                            onPress={() => setShowPassword(!showPassword)}
                        /> 
                        
                    </View>

                </View>

                <View style={login_style.button_container}> 

                    {/* Boton Crear cuenta */}

                    <Button
                        style={login_style.button}
                        textColor="#ffffff"
                        onPress={createAccount}
                    >
                        Crear cuenta
                    </Button>

                    {/* Boton Iniciar sesión */}

                    <Button
                        style={login_style.button}
                        textColor="#ffffff"
                        onPress={singIn}
                    >
                        Iniciar sesión
                    </Button>

                </View>

            </View>

            {/* Mensaje de exito o error */}
            
            <Text style={[login_style.message, {color: messageColor ? 'green' : 'red'}]}>{message}</Text> 

        </View>

    );
};
